CREATE OR REPLACE PROCEDURE gerint_justificativa_leitos ( nr_sequencia_evento_p bigint default 0, dt_internacao_p timestamp default null, ds_justif_data_retroativa_p text default null) AS $body$
DECLARE


qt_registros_w integer;


BEGIN

if	nr_sequencia_evento_p <> 0 then

	select	count(*)
	into STRICT	qt_registros_w
	from	GERINT_EVENTO_INT_DADOS
	where	nr_seq_evento = nr_sequencia_evento_p;

	if	qt_registros_w > 0 then
