SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE germany_custo_image_report ( nr_seq_interno_p text, nr_prescricao_p text, ds_arquivo_imagem_p text, place_order_p text) AS $body$
DECLARE



ie_existe_w	varchar(1);
nr_seq_imagem_w	laudo_paciente_imagem.nr_seq_imagem%TYPE;
nr_seq_laudo_w		bigint;
nr_seq_prescr_w		bigint;
nr_prescr_w		bigint;
ds_pasta_exp_xml_w	varchar(2000);
nm_usuario_w		varchar(255);
nr_seq_empresa_w		bigint;


BEGIN

nm_usuario_w	:= 'CUSTODIAG';
nr_seq_empresa_w	:= 165;

begin

select	max(ds_caminho_entrada)
into STRICT	ds_pasta_exp_xml_w
from    empresa_integr_dados a,
	empresa_integracao b,
	sistema_integracao c
where	a.nr_seq_empresa_integr = b.nr_sequencia
and	b.nr_sequencia = c.nr_seq_empresa
and     b.nr_sequencia = nr_seq_empresa_w;


select
	max(a.nr_sequencia)
into STRICT
	nr_seq_prescr_w
from	prescr_procedimento	a
where	a.nr_prescricao	= nr_prescricao_p
and   a.nr_seq_interno = nr_seq_interno_p;


select	max(a.nr_sequencia)
into STRICT	nr_seq_laudo_w
from	laudo_paciente	a
where	a.nr_prescricao	= nr_prescricao_p
and	a.nr_seq_prescricao	= nr_seq_prescr_w;


SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
INTO STRICT 	ie_existe_w
FROM 	laudo_paciente_imagem a
WHERE 	a.nr_sequencia = nr_seq_laudo_w
AND  	a.ds_arquivo_imagem = ds_arquivo_imagem_p;


IF (ie_existe_w = 'N') THEN
	SELECT	coalesce(MAX(a.nr_seq_imagem),0)+1
	INTO STRICT 	nr_seq_imagem_w
	FROM 	laudo_paciente_imagem a
	WHERE 	a.nr_sequencia = nr_seq_laudo_w;

	INSERT INTO laudo_paciente_imagem(
		nr_sequencia,
		nr_seq_imagem,
		ds_arquivo_imagem,
		nm_usuario,
		dt_atualizacao,
    NM_USUARIO_NREC,
    dt_atualizacao_nrec)
	VALUES ( nr_seq_laudo_w,
		nr_seq_imagem_w,
		ds_pasta_exp_xml_w||ds_arquivo_imagem_p,
		nm_usuario_w,
		clock_timestamp(),
    nm_usuario_w,
    clock_timestamp());

	COMMIT;

END IF;

exception

when others then

	CALL gravar_log_cdi(7000,

			substr(nr_seq_interno_p || ' - ' || nr_prescricao_p || '#@#@' || sqlerrm,1,4000),

			'GERMANY');

end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
