CREATE OR REPLACE PROCEDURE germany_int_update_procedure ( dt_inicio_p text, dt_fim_p text, nr_seq_proc_interno_p text, nr_prescricao_p text, nr_atendimento_p text) AS $body$
DECLARE


dt_liberacao_laudo_w	timestamp;		
ie_tipo_ordem_w		varchar(2) := null;			
cd_medico_laudante_w	varchar(10);
nr_crm_w		varchar(20);
uf_crm_w		varchar(2);
pos_inicio_uf_w		bigint;
nr_prescricao_w     prescr_procedimento.nr_prescricao%type;
qt_reg_w		bigint;

BEGIN

select max(nr_prescricao)
into STRICT    nr_prescricao_w
from    prescr_procedimento
where   nr_seq_interno = nr_prescricao_p;

update	PRESCR_PROCEDIMENTO a
set	DT_INICIO_EXAME = to_date(dt_inicio_p, 'yyyymmddhh24miss'),
	DT_FIM_EXAME = to_date(dt_fim_p, 'yyyymmddhh24miss')
where	a.nr_seq_interno = nr_prescricao_p
and	a.nr_prescricao = nr_prescricao_w
and	exists (SELECT 1
		from	prescr_medica x
		where	x.nr_prescricao = a.nr_prescricao
		and	x.nr_atendimento = nr_atendimento_p);

        
commit;

end;
