SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ger_attach_exam_image_ilara ( nr_laudo_p bigint, ds_arquivo_imagem_p text, nr_seq_empresa_p bigint, nm_usuario_p text, ds_path_p INOUT text) AS $body$
DECLARE



nr_seq_result_lab_w	bigint;
ds_pasta_exp_xml_w	varchar(2000);
nr_seq_prescr_w		bigint;
nr_prescr_w		bigint;
nr_seq_result_w		bigint;
nr_seq_resultado_w	bigint;
nr_seq_result_imagem_w	bigint;
nr_seq_lab_resultado_w	bigint;
nr_prescricao_w		prescr_medica.nr_prescricao%type;
ie_existe_w	varchar(1);
nr_seq_imagem_w	laudo_paciente_imagem.nr_seq_imagem%TYPE;
nm_repository_w varchar(50);


BEGIN

/*This procedure inserts the serial from OBX segment*/

begin

SELECT MAX(NM_PATH),
       MAX(NM_REPOSITORY)
INTO STRICT ds_pasta_exp_xml_w,
     nm_repository_w
FROM FILE_REPOSITORY
WHERE NM_REPOSITORY = 'ILARA';



SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
INTO STRICT 	ie_existe_w
FROM 	laudo_paciente_imagem a
WHERE 	a.nr_sequencia = nr_laudo_p
AND  	a.ds_arquivo_imagem = ds_arquivo_imagem_p;



IF (ie_existe_w = 'N') THEN

	SELECT	coalesce(MAX(a.nr_seq_imagem),0)+1
	INTO STRICT 	nr_seq_imagem_w
	FROM 	laudo_paciente_imagem a
	WHERE 	a.nr_sequencia = nr_laudo_p;

	INSERT INTO laudo_paciente_imagem(
		nr_sequencia,
		nr_seq_imagem,
		ds_arquivo_imagem,
		nm_usuario,
		dt_atualizacao,
		ds_imagem)

	VALUES ( nr_laudo_p,
		nr_seq_imagem_w,
		'tasy-file://'||nm_repository_w||'/'||ds_arquivo_imagem_p,
		'ILARA',
		clock_timestamp(),
		ds_arquivo_imagem_p||'BASE64');

	COMMIT;

	ds_path_p	:= ds_pasta_exp_xml_w||ds_arquivo_imagem_p;

END IF;

exception

when others then

	CALL gravar_log_cdi(7000,
			nr_laudo_p || '#@#@' || '#@#@' || nr_seq_empresa_p || '#@#@' || dbms_utility.format_error_backtrace,
			'GERMANY');
end;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
