CREATE OR REPLACE PROCEDURE ger_cargas_importar_registros ( nr_seq_carga_p ger_carga_inicial.nr_sequencia%type, nr_seq_carga_arq_p ger_carga_arq.nr_sequencia%type, ds_lista_seq_p text default null) AS $body$
DECLARE


lista_seq_w	dbms_sql.varchar2_table;
i		integer;
nr_sequencia_w	bigint;
jobno		bigint;


BEGIN

--Atualiza com a data, para indicar que algum registro ja foi processado ou tentado processar

--Apenas para controle para evitar a exclusao de uma carga com registros ja importados
update	ger_carga_inicial
set	dt_inicio_importacao	= clock_timestamp()
where	nr_sequencia		= nr_seq_carga_p
and	coalesce(dt_inicio_importacao::text, '') = '';

update	ger_carga_inicial
set	ie_acao			= 'IMP' -- Importando
where	nr_sequencia		= nr_seq_carga_p;
commit;

if (ds_lista_seq_p IS NOT NULL AND ds_lista_seq_p::text <> '') then
	lista_seq_w	:=	obter_lista_string(ds_lista_seq_p, ',');
	for i in lista_seq_w.first..lista_seq_w.last loop
		nr_sequencia_w	:= lista_seq_w(i);		
		CALL GER_CARGA_PCK.processar_carga(nr_seq_carga_p,nr_seq_carga_arq_p, nr_sequencia_w,'IMP');
	end loop;
else
	CALL GER_CARGA_PCK.processar_carga(nr_seq_carga_p,nr_seq_carga_arq_p, 1,'IMP');
	commit;
	--GER_CARGA_PCK.processar_carga(nr_seq_carga_p,null,'IMP');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
