CREATE OR REPLACE PROCEDURE ger_carga_excluir ( NR_SEQUENCIA_P GER_CARGA_INICIAL.NR_SEQUENCIA%type, NM_USUARIO_P GER_CARGA_INICIAL.NM_USUARIO%type) AS $body$
DECLARE


jobno		bigint;


BEGIN

--Atualiza com a data, para indicar que algum registro já foi processado ou tentado processar.

--Apenas para controle para evitar a exclusão de uma carga com registros já importados.
update	ger_carga_inicial
