CREATE OR REPLACE PROCEDURE ger_carga_validar_arquivos (nr_seq_carga_p bigint, nr_seq_carga_arq_p bigint, nr_seq_registro_p bigint, nm_usuario_p text, ie_limpar_log_p text default null) AS $body$
DECLARE

nr_seq_registro_w	bigint;
jobno			bigint;
	

BEGIN

update	ger_carga_inicial
set	ie_acao		= 'VAL' --Validando
where	nr_sequencia	= nr_seq_carga_p;
commit;

nr_seq_registro_w	:= null;
if (coalesce(nr_seq_registro_p,0) > 0) then
	nr_seq_registro_w	:= nr_seq_registro_p;
end if;

if (coalesce(ie_limpar_log_p,'N') = 'S') then

	delete from ger_carga_log_import
	where	nr_seq_carga	= nr_seq_carga_p;
end if;

dbms_job.submit(jobno, 'ger_carga_pck.processar_carga('''|| nr_seq_carga_p || ''',''' || nr_seq_carga_arq_p || ''',''' || nr_seq_registro_w || ''',''VAL'');');
--ger_carga_pck.PROCESSAR_CARGA(nr_seq_carga_p,nr_seq_registro_w,'VAL');
commit;

end;
$body$
