CREATE OR REPLACE PROCEDURE ger_desfazer_cancel_cheque (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_cancel_w	smallint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	count(*)
	into STRICT	qt_existe_cancel_w
	from	cheque
	where	nr_sequencia = nr_sequencia_p
	and	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '');

	if (qt_existe_cancel_w > 0) then

		update	cheque
		set	dt_cancelamento  = NULL,
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_sequencia_p;

		commit;

		end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
