SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ger_limpar_dt_impressao_cheque (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_imp_w		smallint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	count(*)
	into STRICT	qt_existe_imp_w
	from	cheque
	where	nr_sequencia = nr_sequencia_p
	and	(dt_impressao IS NOT NULL AND dt_impressao::text <> '');

	if (qt_existe_imp_w > 0) then

		update 	cheque
		set 	dt_impressao  = NULL,
			nm_usuario = nm_usuario_p
		where 	nr_sequencia = nr_sequencia_p;

		commit;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
