CREATE OR REPLACE PROCEDURE gestao_amostra_beforepost ( nr_seq_tipo_p text, nr_prescricao_p bigint, nr_seq_lab_p text, nm_usuario_p text, ie_tipo_exame_p INOUT text, ie_amostra_cong_p INOUT text, qt_amostra_prescr_p INOUT bigint ) AS $body$
BEGIN

ie_tipo_exame_p 	:= '';
ie_amostra_cong_p 	:= '';
qt_amostra_prescr_p	:= 0;

if (nr_seq_tipo_p IS NOT NULL AND nr_seq_tipo_p::text <> '') then

	select 	coalesce(ie_tipo_exame,0)
	into STRICT	ie_tipo_exame_p
	from 	tipo_amostra
	where 	nr_sequencia = nr_seq_tipo_p;

end if;

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	/*
	select  count(*)
	into	qt_congelamento_p
	from    prescr_proc_peca a,
		tipo_amostra b
	where   obter_atendimento_prescr(a.nr_prescricao) = obter_atendimento_prescr(nr_prescricao_p)
	and     a.nr_seq_tipo 	= b.nr_sequencia
	and     b.ie_tipo_exame = 5;
	*/
	ie_amostra_cong_p := obter_se_congelacao_existe(nr_seq_lab_p, nr_prescricao_p);

	select 	count(*)
	into STRICT	qt_amostra_prescr_p
	from 	prescr_proc_peca
	where 	nr_prescricao = nr_prescricao_p;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
