SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gestvg_atualizar_hist_padrao ( nr_sequencia_p bigint, ie_atualizar_p text, ds_text_p text, nm_usuario_p text) AS $body$
DECLARE


ds_conteudo_1_w		varchar(32764);
ds_conteudo_2_w		varchar(32764);
ds_campo_long_w		text;


BEGIN
if (coalesce(nr_sequencia_p,0) > 0) and (ie_atualizar_p IS NOT NULL AND ie_atualizar_p::text <> '') then

	if (ie_atualizar_p = 'H') then
	
		select ds_historico
		into STRICT ds_campo_long_w
		from gestao_vaga_hist
		where	nr_sequencia = nr_sequencia_p;
		
		ds_conteudo_1_w := substr(ds_campo_long_w,32764,1);
		ds_conteudo_2_w := substr(ds_campo_long_w,32764,32765);
		
		update	Gestao_vaga_hist
		set	ds_historico = ds_conteudo_1_w  || ds_conteudo_2_w ||' ' || ds_text_p
		where	nr_sequencia = nr_sequencia_p;
		
	else
	
		update	gestao_vaga_hist
		set	ds_observacao = substr(ds_observacao||' '||ds_text_p,1,255)
		where	nr_sequencia = nr_sequencia_p;
		
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
