CREATE OR REPLACE PROCEDURE get_axis_value_param_apap (nr_seq_param_p bigint, vl_axis_min_p INOUT bigint, vl_axis_max_p INOUT bigint) AS $body$
DECLARE


i	integer;
hr_w	varchar(2);
ds_sql_w	varchar(2000);
vl_w	varchar(255);
vl_min_w	varchar(255) := 99999;
vl_max_w	varchar(255) := -99999;


BEGIN

if (nr_seq_param_p IS NOT NULL AND nr_seq_param_p::text <> '') then

	i	:= -1;

	while(i < 23) loop
		begin

		i	:= i + 1;

		if (i < 10) then
			hr_w	:= '0' || i;
		else
			hr_w	:= i;
		end if;

		ds_sql_w	:=	'select	ds_hora_' || hr_w || ' 				'||
					'from	w_pep_apap			'||
					'where	nr_sequencia = :nr_seq_param	';

		vl_w := obter_valor_dinamico_bv(ds_sql_w, 'nr_seq_param='||nr_seq_param_p, vl_w);

		if ((vl_w)::numeric  > 0) and ((vl_w)::numeric  < (vl_min_w)::numeric ) then
			vl_min_w	:= vl_w;
		end if;

		if ((vl_w)::numeric  > 0) and ((vl_w)::numeric  > (vl_max_w)::numeric ) then
			vl_max_w	:= vl_w;
		end if;


		end;
	end loop;

end if;

vl_axis_min_p	:= vl_min_w;
