CREATE OR REPLACE PROCEDURE get_free_time_list_arg ( cd_estabelecimento_p bigint, cd_lista_agenda_p text, dt_agenda_p timestamp, dt_final_p timestamp, qt_minutos_p bigint, hr_inicial_p text, hr_final_p text, ds_restricao_p text, nm_usuario_p text, ie_grava_livre_p text, ds_horarios_p INOUT text, ie_gerar_retroativo_p text DEFAULT 'N', cd_departamento_medico_p bigint DEFAULT NULL, cd_departamento_medico_lista_p text DEFAULT NULL) AS $body$
DECLARE


    cd_agenda_w           bigint;
    ds_horarios_w         varchar(4000);
    ie_horario_passado_w  agenda.ie_horario_passado%TYPE;
    c01 CURSOR FOR
    SELECT
        cd_agenda,
        ie_horario_passado
    FROM
        agenda
    WHERE
        obter_se_contido(cd_agenda, cd_lista_agenda_p) = 'S';


BEGIN
    OPEN c01;
