                INTO STRICT ie_horario_passado_w
;

                ds_horarios_w := get_surgery_free_time_arg(cd_estabelecimento_p, cd_agenda_w, dt_agenda_p, dt_final_p, qt_minutos_p, hr_inicial_p, hr_final_p, ds_restricao_p, nm_usuario_p, ie_grava_livre_p, ds_horarios_w, ie_horario_passado_w, cd_departamento_medico_p, cd_departamento_medico_lista_p);

                ds_horarios_p := ds_horarios_w;
            END;
        END;

