CREATE OR REPLACE PROCEDURE get_storage_data ( ds_path_p file_storage_path.ds_path%type, cd_empresa_p file_storage_path.cd_empresa%type, cd_estabelecimento_p file_storage_path.cd_estabelecimento%type, cd_perfil_p file_storage_path.cd_perfil%type, cd_setor_atendimento_p file_storage_path.cd_setor_atendimento%type, nm_usuario_p file_storage_path.nm_usuario_path%type, ds_path_out_p INOUT file_storage_path.ds_path%type, cd_uuid_out_p INOUT file_storage_path.cd_uuid%type) AS $body$
