SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ge_desfazer_exec_proc (nr_seq_interno_p bigint) AS $body$
DECLARE


nr_count_procpaci_w 	integer;
nr_prescricao_w 		prescr_procedimento.nr_prescricao%type;
nr_seq_prescricao_w     prescr_procedimento.nr_sequencia%type;


BEGIN
	select count(*)
	into STRICT nr_count_procpaci_w
	from prescr_procedimento pp
	join procedimento_paciente pro
	ON (pp.nr_prescricao = pro.nr_prescricao
	and pp.nr_sequencia = pro.nr_sequencia_prescricao)
  where pp.nr_seq_interno = nr_seq_interno_p;

	if (nr_count_procpaci_w > 0) then
		select nr_prescricao, nr_sequencia
		into STRICT nr_prescricao_w, nr_seq_prescricao_w
		from prescr_procedimento
		where nr_seq_interno = nr_seq_interno_p;

		CALL desfazer_execucao_procedimento(nr_prescricao_w, nr_seq_prescricao_w, 'GE_Centricity');
	end if;

	update prescr_procedimento
	set ie_status_execucao = '10',
	dt_cancelamento = clock_timestamp()
  	where nr_seq_interno = nr_seq_interno_p;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
