SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE glosar_confirmar_guia (nr_seq_ret_item_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* 
ie_opcao_p 
G - Glosar 
C - Confirmar 
*/
 
 
vl_saldo_w		double precision;


BEGIN 
 
select	coalesce(max(obter_saldo_conpaci(nr_interno_conta,cd_autorizacao)),0) 
into STRICT	vl_saldo_w 
from	convenio_retorno_item 
where	nr_sequencia	= nr_seq_ret_item_p;
 
if (ie_opcao_p = 'G') then 
	update	convenio_retorno_item 
	set	vl_pago		= 0, 
		ie_glosa 	= 'S', 
		vl_glosado 	= vl_saldo_w, 
		nm_usuario 	= nm_usuario_p, 
		dt_atualizacao 	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_ret_item_p;
elsif (ie_opcao_p = 'C') then 
	update	convenio_retorno_item 
	set	vl_pago		= vl_saldo_w, 
		ie_glosa	= 'N', 
		vl_glosado	= 0, 
		nm_usuario 	= nm_usuario_p, 
		dt_atualizacao 	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_ret_item_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
