CREATE OR REPLACE PROCEDURE gm_atualiza_amostra_princ_peca ( nr_seq_peca_p bigint, nr_seq_amostra_princ_p bigint, cd_topografia_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_macroscopia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_proc_macroscopia
	where	nr_seq_peca	= nr_seq_peca_p;


BEGIN

update	prescr_proc_peca
set	nr_seq_amostra_princ	= nr_seq_amostra_princ_p,
	cd_topografia		= cd_topografia_p,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_seq_peca		= nr_seq_peca_p;


update 	prescr_proc_macroscopia
set	dt_liberacao 	 = NULL,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_seq_peca 	= nr_seq_peca_p
and	coalesce(dt_inativacao::text, '') = '';


open C01;
loop
fetch C01 into
	nr_seq_macroscopia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	prescr_proc_macro_desig
	set	nr_seq_amostra_princ   	= nr_seq_amostra_princ_p,
		ie_alterar_desc		= 'S',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_macroscopia	= nr_seq_macroscopia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
