SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gm_inativar_amostra_peca (nr_prescricao_p bigint, nr_seq_peca_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
  ie_opcao_p  = 'S'    -  inativar peças
  ie_opcao_p  = 'N'  -  desfazer inativação
*/
nr_sequencia_peca_w    bigint;
nr_sequencia_macrosc_w    bigint;
nr_sequencia_designacao_w  bigint;

C01 CURSOR FOR
  SELECT nr_sequencia
  FROM   prescr_proc_peca
  WHERE  nr_seq_peca = nr_seq_peca_p
  AND    nr_prescricao = nr_prescricao_p
  order by nr_sequencia;

C02 CURSOR FOR
  SELECT  b.nr_sequencia,
    a.nr_sequencia
  FROM   PRESCR_PROC_MACROSCOPIA a,
    prescr_proc_macro_desig b
  WHERE   a.nr_seq_peca = nr_seq_peca_p
  AND     a.nr_sequencia = b.nr_seq_macroscopia;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_peca_p IS NOT NULL AND nr_seq_peca_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
  if (ie_opcao_p = 'S') then
    begin
      open C01;
      loop
      fetch C01 into
        nr_sequencia_peca_w;
      EXIT WHEN NOT FOUND; /* apply on C01 */
        begin
          update  prescr_proc_peca
          set  ie_situacao = 'I',
            nm_usuario_inativacao = nm_usuario_p,
            dt_inativacao = clock_timestamp()
          where   nr_prescricao = nr_prescricao_p
          and  nr_sequencia = nr_sequencia_peca_w;
        end;
      end loop;
      close C01;

      open C02;
      loop
      fetch C02 into
        nr_sequencia_designacao_w,
        nr_sequencia_macrosc_w;
      EXIT WHEN NOT FOUND; /* apply on C02 */
        begin
          update  prescr_proc_macro_desig
          set  nm_usuario_inativacao = nm_usuario_p,
            dt_inativacao = clock_timestamp()
          where   nr_sequencia = nr_sequencia_designacao_w
          and  NR_SEQ_MACROSCOPIA = nr_sequencia_macrosc_w;
        end;
      end loop;
      close C02;

      update  prescr_proc_peca
      set  ie_situacao = 'I',
        nm_usuario_inativacao = nm_usuario_p,
        dt_inativacao = clock_timestamp()
      where   nr_prescricao = nr_prescricao_p
      and  nr_sequencia = nr_seq_peca_p;

      update  prescr_proc_macroscopia
      set  nm_usuario_inativacao = nm_usuario_p,
        dt_inativacao = clock_timestamp()
      where   nr_seq_peca = nr_seq_peca_p;
    end;
  else
    begin
      open C01;
      loop
      fetch C01 into
        nr_sequencia_peca_w;
      EXIT WHEN NOT FOUND; /* apply on C01 */
        begin
          update  prescr_proc_peca
          set  ie_situacao = 'A',
            nm_usuario_inativacao = '',
            dt_inativacao  = NULL
          where   nr_prescricao = nr_prescricao_p
          and  nr_sequencia = nr_sequencia_peca_w;
        end;
      end loop;
      close C01;

      open C02;
      loop
      fetch C02 into
        nr_sequencia_designacao_w,
        nr_sequencia_macrosc_w;
      EXIT WHEN NOT FOUND; /* apply on C02 */
        begin
          update  prescr_proc_macro_desig
          set  nm_usuario_inativacao = '',
            dt_inativacao  = NULL
          where   nr_sequencia = nr_sequencia_designacao_w
          and  NR_SEQ_MACROSCOPIA = nr_sequencia_macrosc_w;
        end;
      end loop;
      close C02;
    update  prescr_proc_peca
    set  ie_situacao = 'A',
      nm_usuario_inativacao = '',
      dt_inativacao  = NULL
    where   nr_prescricao = nr_prescricao_p
    and  nr_sequencia = nr_seq_peca_p;

    update  prescr_proc_macroscopia
    set  nm_usuario_inativacao = '',
      dt_inativacao  = NULL
    where   nr_seq_peca = nr_seq_peca_p;
    end;
  end if;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gm_inativar_amostra_peca (nr_prescricao_p bigint, nr_seq_peca_p bigint, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

