CREATE OR REPLACE PROCEDURE gm_inativar_designacao (nr_seq_designacao_p bigint, nm_usuario_p text) AS $body$
BEGIN


if (nr_seq_designacao_p IS NOT NULL AND nr_seq_designacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update 	prescr_proc_macro_desig
	set	nm_usuario_inativacao   = nm_usuario_p,
	dt_inativacao  		= clock_timestamp()
	where	nr_sequencia		= nr_seq_designacao_p;

	update PRESCR_PROC_PECA
	set	ie_status = 'C'
	where	nr_seq_designacao = nr_seq_designacao_p;


	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
