CREATE OR REPLACE PROCEDURE gm_liberar_designacao (nr_seq_designacao_p bigint, nr_seq_peca_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_designacao_w	varchar(10);
ie_gerar_lamina_w	varchar(1);


BEGIN

if (nr_seq_designacao_p IS NOT NULL AND nr_seq_designacao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update 	prescr_proc_macro_desig
	set	nm_usuario_liberacao  = nm_usuario_p,
		dt_liberacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_designacao_p;

	select	max(ie_tipo_designacao),
		max(ie_gerar_lamina)
	into STRICT	ie_tipo_designacao_w,
		ie_gerar_lamina_w
	from	prescr_proc_macro_desig
	where	nr_sequencia		= nr_seq_designacao_p;

	if (ie_tipo_designacao_w	= 'B') and (ie_gerar_lamina_w	= 'S') then
		CALL gm_duplicar_sub_amostras_desig(nr_seq_designacao_p, nr_seq_peca_p, 'L',nm_usuario_p);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
