SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gm_liberar_macroscopia (nr_seq_macroscopia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_designacao_w	varchar(10);
ie_gerar_lamina_w	varchar(1);
nr_seq_peca_w		bigint;
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_proc_macro_desig
	where	nr_seq_macroscopia	= nr_seq_macroscopia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');


BEGIN

if (nr_seq_macroscopia_p IS NOT NULL AND nr_seq_macroscopia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	update 	prescr_proc_macroscopia
	set	nm_usuario_lib  = nm_usuario_p,
		dt_liberacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_macroscopia_p;

	update	prescr_proc_macro_desig
	set	nm_usuario_liberacao  	= nm_usuario_p,
		dt_liberacao		= clock_timestamp()
	where	nr_seq_macroscopia	= nr_seq_macroscopia_p
	and	coalesce(dt_liberacao::text, '') = '';

	select	max(nr_seq_peca)
	into STRICT	nr_seq_peca_w
	from	prescr_proc_macroscopia
	where	nr_sequencia	= nr_seq_macroscopia_p;


	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		select	max(ie_tipo_designacao),
			max(ie_gerar_lamina)
		into STRICT	ie_tipo_designacao_w,
			ie_gerar_lamina_w
		from	prescr_proc_macro_desig
		where	nr_sequencia		= nr_sequencia_w;

		if (ie_tipo_designacao_w	= 'B') and (ie_gerar_lamina_w	= 'S') then
			CALL gm_duplicar_sub_amostras_desig(nr_sequencia_w, nr_seq_peca_w, 'L',nm_usuario_p);
		end if;
	end loop;
	close c01;


end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gm_liberar_macroscopia (nr_seq_macroscopia_p bigint, nm_usuario_p text) FROM PUBLIC;

