CREATE OR REPLACE PROCEDURE gpi_alterar_vl_realizado ( nr_seq_orc_item_p bigint, vl_realizado_atual_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_realizado_ant_w		bigint;


BEGIN

select	coalesce(vl_realizado,0)
into STRICT	vl_realizado_ant_w
from	gpi_orc_item
where	nr_sequencia = nr_seq_orc_item_p;

update	gpi_orc_item
set	vl_realizado = vl_realizado_atual_p
where	nr_sequencia = nr_seq_orc_item_p;

insert	into gpi_log_realizado( 	nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				vl_realizado_ant,
				nr_seq_orc_item)
		values (		nextval('gpi_log_realizado_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				vl_realizado_ant_w,
				nr_seq_orc_item_p);

commit;

end;
