CREATE OR REPLACE PROCEDURE gpi_atualizar_cronograma ( nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_inicio_prev_w			timestamp;
dt_fim_prev_w			timestamp;
pr_real_w			double precision;


BEGIN

select	min(a.dt_inicio_prev),
	max(a.dt_fim_prev)
into STRICT	dt_inicio_prev_w,
	dt_fim_prev_w
from	gpi_cron_etapa a
where	nr_seq_cronograma		= nr_seq_cronograma_p;

pr_real_w	:= gpi_obter_perc_cron(nr_seq_cronograma_p, nm_usuario_p);

update	gpi_cronograma
set	dt_inicio_prev	= dt_inicio_prev_w,
	dt_fim_prev	= dt_fim_prev_w,
	pr_real		= pr_real_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_cronograma_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
