SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_atualizar_dt_prev_etapa ( nr_sequencia_p bigint, dt_prevista_p timestamp, dt_inicio_prev_p timestamp, dt_fim_prev_p timestamp, ie_data_alterar_p bigint, ie_forma_alteracao_p bigint, ie_acao_dias_p bigint, nr_dias_p bigint, nm_usuario_p text) AS $body$
DECLARE



dt_prevista_w		timestamp;
dt_inicio_prev_w		timestamp;
dt_fim_prev_w		timestamp;
nr_dias_w		bigint;
ie_atualiza_superior_w	varchar(1);


BEGIN

nr_dias_w		:= obter_dias_entre_datas(dt_inicio_prev_p, dt_fim_prev_p);
ie_atualiza_superior_w	:= substr(coalesce(obter_valor_param_usuario(7027, 22, obter_perfil_ativo, nm_usuario_p, coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)),'N'),1,1);

if (ie_forma_alteracao_p = 1) then
	begin
	dt_inicio_prev_w	:= dt_inicio_prev_p;
	dt_fim_prev_w	:= dt_fim_prev_p;
	if (ie_data_alterar_p = 0) then
		begin
		dt_inicio_prev_w	:= dt_prevista_p;
		dt_fim_prev_w	:= dt_prevista_p + nr_dias_w;
		end;
	elsif (ie_data_alterar_p = 1) then
		begin
		dt_fim_prev_w	:= dt_prevista_p;
		dt_inicio_prev_w	:= trunc(dt_fim_prev_w,'dd') - nr_dias_w;
		end;
	end if;

	update	gpi_cron_etapa
	set	dt_inicio_prev	= dt_inicio_prev_w,
		dt_fim_prev	= dt_fim_prev_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
	end;
else
	begin
	update	gpi_cron_etapa
	set	dt_inicio_prev	= CASE WHEN ie_acao_dias_p=0 THEN dt_inicio_prev-nr_dias_p  ELSE dt_inicio_prev+nr_dias_p END ,
		dt_fim_prev	= CASE WHEN ie_acao_dias_p=0 THEN dt_fim_prev-nr_dias_p  ELSE dt_fim_prev+nr_dias_p END ,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
	end;
end if;

if (ie_atualiza_superior_w = 'S') then
	CALL gpi_atualizar_etapa_superior(nr_sequencia_p, nm_usuario_p, null);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
