CREATE OR REPLACE PROCEDURE gpi_atualizar_hora_real_etapa ( nr_seq_etapa_p bigint, qt_hora_real_p bigint, nm_usuario_p text) AS $body$
BEGIN

update	gpi_cron_etapa
set	qt_hora_real	= qt_hora_real_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_etapa_p;

CALL gpi_atualizar_etapa_superior(	nr_seq_etapa_p,
			nm_usuario_p); /*(Recursiva)*/
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
