SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_atualizar_real_ativ ( nr_seq_ordem_serv_p bigint, nr_seq_etapa_gpi_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_hora_real_w			double precision	:= 0;
qt_total_minuto_w			bigint;
nr_seq_proj_gpi_w			bigint;


BEGIN

select	max(b.nr_seq_projeto)
into STRICT	nr_seq_proj_gpi_w
from	gpi_cronograma b,
	gpi_cron_etapa a
where	a.nr_seq_cronograma	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_etapa_gpi_p;

select	coalesce(sum(a.qt_minuto),0)
into STRICT	qt_total_minuto_w
from	man_ordem_serv_ativ a,
	man_ordem_servico b
where	a.nr_seq_ordem_serv	= b.nr_sequencia
and	b.nr_seq_etapa_gpi		= nr_seq_etapa_gpi_p
and	b.nr_seq_proj_gpi		= nr_seq_proj_gpi_w
and	coalesce(a.qt_minuto,0)	<> 0;

qt_hora_real_w	:= dividir(qt_total_minuto_w,60);

update	gpi_cron_etapa
set	qt_hora_real	= qt_hora_real_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_etapa_gpi_p;

/*Atenção - recursiva*/

CALL gpi_atualizar_etapa_superior(nr_seq_etapa_gpi_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
