CREATE OR REPLACE PROCEDURE gpi_atualizar_tempo_fase ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_superior_w		bigint;
nr_seq_superior_ww	bigint;
dt_fim_fase_w		timestamp;
dt_inicio_fase_w		timestamp;
qt_horas_fase_w		double precision;


BEGIN

select	max(nr_seq_superior)
into STRICT	nr_seq_superior_w
from	gpi_cron_etapa
where	nr_sequencia = nr_sequencia_p;

select	max(dt_fim_prev),
	min(dt_inicio_prev),
	sum(qt_hora_prev)
into STRICT	dt_fim_fase_w,
	dt_inicio_fase_w,
	qt_horas_fase_w
from	gpi_cron_etapa
where	nr_seq_superior = nr_seq_superior_w;

update	gpi_cron_etapa
set	dt_fim_prev	= dt_fim_fase_w,
	dt_inicio_prev	= dt_inicio_fase_w,
	qt_hora_prev	= qt_horas_fase_w
where	nr_sequencia	= nr_seq_superior_w;

commit;

if (nr_seq_superior_w <> 0) then
	CALL gpi_atualizar_tempo_fase(nr_seq_superior_w, nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
