SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_atualiza_dt_prev_proj_cron ( nr_seq_projeto_p bigint, nr_seq_cronograma_p bigint) AS $body$
DECLARE


nr_seq_projeto_w	bigint;
dt_inicio_prev_w	timestamp;
dt_fim_prev_w		timestamp;
qt_registro_w		bigint;


BEGIN

--Atualizar Datas previstas Cronograma:
select 	count(*)
into STRICT	qt_registro_w
from	gpi_cron_etapa
where	nr_seq_cronograma 	= nr_seq_cronograma_p;

if (qt_registro_w > 0) then
	select 	trunc(min(dt_inicio_prev)),
		trunc(max(dt_fim_prev))
	into STRICT	dt_inicio_prev_w,
		dt_fim_prev_w
	from	gpi_cron_etapa
	where	nr_seq_cronograma 	= nr_seq_cronograma_p;

	update 	gpi_cronograma
	set	dt_inicio_prev	= dt_inicio_prev_w,
		dt_fim_prev  	= dt_fim_prev_w
	where	nr_sequencia	= nr_seq_cronograma_p;

--Atualizar Datas previstas Projeto
	select 	trunc(min(a.dt_inicio_prev)),
		trunc(max(a.dt_fim_prev))
	into STRICT	dt_inicio_prev_w,
		dt_fim_prev_w
	from	gpi_cron_etapa a
	where	nr_seq_cronograma in (	SELECT 	nr_sequencia
				from	gpi_cronograma
				where	nr_seq_projeto = nr_seq_projeto_p);

	update 	gpi_projeto
	set 	dt_inicio_prev 	= dt_inicio_prev_w,
		dt_fim_prev	= dt_fim_prev_w
	where	nr_sequencia	= nr_seq_projeto_p;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
