CREATE OR REPLACE PROCEDURE gpi_copiar_equipe_ata ( nr_seq_equipe_membro_p bigint, nr_seq_ata_p bigint, nm_usuario_p text) AS $body$
DECLARE

			 
nm_pessoa_fisica_w		varchar(60);
cd_pessoa_fisica_w		varchar(10);

c01 CURSOR FOR 
SELECT	a.cd_pessoa_fisica, 
	substr(obter_nome_pf(a.cd_pessoa_fisica),1,60) nm_pessoa_fisica 
from	gpi_proj_equipe_membro a 
where	a.nr_seq_equipe = nr_seq_equipe_membro_p 
and	not exists (	SELECT	1 
			from	proj_ata_participante y 
			where	y.nr_seq_ata		= nr_seq_ata_p 
			and	y.cd_pessoa_participante	= a.cd_pessoa_fisica);
			

BEGIN 
 
open c01;
loop 
fetch c01 into 
	cd_pessoa_fisica_w, 
	nm_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	insert into proj_ata_participante( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_ata, 
		ie_faltou, 
		nm_pessoa_participante, 
		ds_setor, 
		cd_pessoa_participante, 
		dt_ciente_ata, 
		cd_setor_atendimento, 
		ds_observacao) 
	values (	nextval('proj_ata_participante_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_ata_p, 
		'N', 
		nm_pessoa_fisica_w, 
		null, 
		cd_pessoa_fisica_w, 
		null, 
		null, 
		null);
	end;		
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
