CREATE OR REPLACE PROCEDURE gpi_definir_valor_orc_item ( nr_seq_orcamento_p bigint, cd_estabelecimento_p bigint, qt_dia_p bigint, ie_sobrepor_p text, nm_usuario_p text) AS $body$
DECLARE


/*Procedure criada para definir o valor ORÇADO dos itens do orçamento
Inicialmente, busca o valor da última compra.*/
nr_sequencia_w			bigint;
cd_material_w			bigint;
vl_unitario_novo_w			double precision;
vl_unitario_w			double precision;
vl_orcado_w			double precision;
nr_seq_nota_fiscal_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_material,
	coalesce(vl_unitario,0)
from	gpi_orc_item
where	nr_seq_orcamento	= nr_seq_orcamento_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_material_w,
	vl_unitario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(a.nr_sequencia) nr_seq_nota_fiscal,
		max(b.vl_unitario_item_nf) vl_unitario_novo
	into STRICT	nr_seq_nota_fiscal_w,
		vl_unitario_novo_w
	from	nota_fiscal a,
		nota_fiscal_item b
	where	a.nr_sequencia		= b.nr_sequencia
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_material		= cd_material_w
	and	a.nr_sequencia = (
		SELECT	max(x.nr_sequencia)
		from	natureza_operacao n,
			nota_fiscal x,
			nota_fiscal_item y
		where	x.nr_sequencia = y.nr_sequencia
		and	n.cd_natureza_operacao	= x.cd_natureza_operacao
		and	n.ie_entrada_saida		= 'E'
		and	x.dt_emissao 		>= clock_timestamp() - coalesce(qt_dia_p,90)
		and	x.ie_acao_nf 		= 1
		and	y.cd_material		= cd_material_w
		and	y.cd_estabelecimento 	= cd_estabelecimento_p);

	if (coalesce(vl_unitario_novo_w,0) <> 0) and
		((vl_unitario_w = 0) or
		(vl_unitario_w <> 0 AND ie_sobrepor_p = 'S')) then

		update	gpi_orc_item
		set	vl_unitario		= vl_unitario_novo_w,
			vl_orcado			= (coalesce(qt_item,0) * vl_unitario_novo_w),
			nr_seq_nota_fiscal		= nr_seq_nota_fiscal_w,
			ie_origem_orc		= 'C'
		where	nr_sequencia		= nr_sequencia_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
