CREATE OR REPLACE PROCEDURE gpi_duplicar_orcamento ( nr_seq_orcamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_novo_orc_w		bigint;

BEGIN

select	nextval('gpi_orcamento_seq')
into STRICT	nr_seq_novo_orc_w
;

insert into gpi_orcamento(
	nr_sequencia,
	nr_seq_projeto,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_orcamento,
	dt_orcamento,
	dt_liberacao,
	nm_usuario_lib,
	dt_aprovacao,
	nm_usuario_aprov,
	ie_preliminar_definitivo,
	ie_situacao,
	nr_seq_cronograma)
SELECT	nr_seq_novo_orc_w,
	nr_seq_projeto,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_orcamento,
	clock_timestamp(),
	null,
	null,
	null,
	null,
	'P',
	'A',
	nr_seq_cronograma
from	gpi_orcamento
where	nr_sequencia	= nr_seq_orcamento_p;

insert into gpi_orc_item(
	nr_sequencia,
	nr_seq_orcamento,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_plano,
	vl_orcado,
	cd_material,
	qt_item,
	vl_unitario,
	cd_unidade_medida,
	nr_seq_etapa,
	cd_centro_custo,
	ds_compl_item,
	ds_modelo,
	ds_fornecedor,
	vl_realizado)
SELECT	nextval('gpi_orc_item_seq'),
	nr_seq_novo_orc_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_plano,
	vl_orcado,
	cd_material,
	qt_item,
	vl_unitario,
	cd_unidade_medida,
	nr_seq_etapa,
	cd_centro_custo,
	ds_compl_item,
	ds_modelo,
	ds_fornecedor,
	0
from	gpi_orc_item
where	nr_seq_orcamento	= nr_seq_orcamento_p;
commit;

end;
$body$
