CREATE OR REPLACE PROCEDURE gpi_enviar_comunic_historico ( nr_sequencia_p bigint, ds_setor_adicional_p text, ds_perfil_adicional_p text, nr_seq_equipe_p bigint, nm_usuario_dest_p text, nm_usuario_p text) AS $body$
DECLARE

				
ds_titulo_w		varchar(255);
ds_historico_w		varchar(2000);
ds_destino_w		varchar(255);
ie_contido_w		bigint;
nm_usuario_dest_w		varchar(255);
nm_usuario_equipe_w	varchar(15);
nr_seq_projeto_w		bigint;
ds_texto_w		text;

C01 CURSOR FOR
SELECT	b.nm_usuario
from	usuario b,
	gpi_proj_equipe_membro a
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	b.ie_situacao	= 'A'
and	a.nr_seq_equipe	= nr_seq_equipe_p
order by	b.nm_usuario;


BEGIN

ds_destino_w		:= '';
nm_usuario_equipe_w	:= '';
nm_usuario_dest_w		:= substr(nm_usuario_dest_p,1,255);

if (coalesce(nm_usuario_dest_w,'X') <> 'X') then
	ds_destino_w	:= substr(ds_destino_w || nm_usuario_dest_w||',',1,255);
end if;

select	substr(coalesce(a.ds_titulo, gpi_obter_nome_projeto(a.nr_seq_projeto)),1,255),
	a.ds_texto,
	a.nr_seq_projeto
into STRICT	ds_titulo_w,
	ds_texto_w,
	nr_seq_projeto_w
from	gpi_projeto_hist a
where	a.nr_sequencia	= nr_sequencia_p;

if (coalesce(nr_seq_equipe_p,0) <> 0) then
	open C01;
	loop
	fetch C01 into	
		nm_usuario_equipe_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_contido_w		:= 0;
		ie_contido_w		:= coalesce(position(nm_usuario_equipe_w in ds_destino_w),0);
		if (ie_contido_w = 0) then	
			ds_destino_w	:= substr(ds_destino_w || nm_usuario_equipe_w || ',',1,255);
		end if;
		end;
	end loop;
	close C01;
end if;

ds_destino_w	:= substr(ds_destino_w, 1, (length(ds_destino_w) -1));

	CALL gerar_comunic_padrao(clock_timestamp(),
			ds_titulo_w,
			ds_texto_w,
			nm_usuario_p,
			'N',
			ds_destino_w,
			'N',
			null,
			ds_perfil_adicional_p,
			null,
			ds_setor_adicional_p,
			clock_timestamp(),
			'',
			'',
			'N');

if (coalesce(ds_destino_w::text, '') = '') then
	if (ds_perfil_adicional_p IS NOT NULL AND ds_perfil_adicional_p::text <> '') then
		ds_destino_w	:= substr(ds_perfil_adicional_p,1,255);
	end if;
	if (ds_setor_adicional_p IS NOT NULL AND ds_setor_adicional_p::text <> '') then
		ds_destino_w	:= substr(ds_destino_w || ' ' || ds_setor_adicional_p,1,255);
	end if;
	
end if;
	CALL gpi_gravar_log_envio(nr_seq_projeto_w, ds_destino_w, 'HIS', 'CI', nr_sequencia_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
