SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_excluir_cronograma ( nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_titulo_w			varchar(100);
dt_aprovacao_w			timestamp;


BEGIN

select	max(ds_titulo),
	max(dt_aprovacao)
into STRICT	ds_titulo_w,
	dt_aprovacao_w
from	gpi_cronograma
where	nr_sequencia	= nr_seq_cronograma_p;

if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
	/*Não é possível excluir um cronograma já aprovado!');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266552);
end if;

delete	from gpi_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p;

delete	from gpi_cron_estrut
where	nr_seq_cronograma = nr_seq_cronograma_p;

delete from gpi_cronograma
where	nr_sequencia	= nr_seq_cronograma_p;

insert into ctb_log(
	cd_log,
	ds_log,
	nm_usuario,
	dt_atualizacao)
values (	55747,
	wheb_mensagem_pck.get_texto(303490) || nr_seq_cronograma_p || '/' || ds_titulo_w, -- Excluído cronograma:
	nm_usuario_p,
	clock_timestamp());

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
