SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_excluir_importar_cron_js ( nr_seq_cronograma_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE



ds_titulo_w		varchar(255)	:= '';
dt_aprovacao_w		timestamp;
ds_erro_w		varchar(255)	:= '';
nr_seq_crono_w		bigint;
qt_registro_w		bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	gpi_cron_etapa a
	where	a.nr_seq_cronograma = nr_seq_crono_w
	and	coalesce(a.nr_seq_superior::text, '') = '';

c01_w		c01%rowtype;WITH RECURSIVE cte AS (


c02 CURSOR FOR
	SELECT	a.nr_sequencia,1 as level
	from	gpi_cron_etapa a WHERE a.nr_sequencia  = c01_w.nr_sequencia
  UNION ALL


c02 CURSOR FOR
	SELECT	a.nr_sequencia,(c.level+1)
	from	gpi_cron_etapa a JOIN cte c ON (c.prior nr_sequencia = a.a.nr_seq_superior)

) SELECT * FROM cte ORDER BY  level desc;
;


c02_w		c02%rowtype;


BEGIN

select	max(ds_titulo),
	max(dt_aprovacao)
into STRICT	ds_titulo_w,
	dt_aprovacao_w
from	gpi_cronograma
where	nr_sequencia		= nr_seq_cronograma_p;

if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then

	ds_erro_w		:=  substr(obter_texto_tasy(94772, null),1,255);
else
	qt_registro_w := 0;
	open c01;
	loop
	fetch c01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		open c02;
		loop
		fetch c02 into
			c02_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			qt_registro_w := qt_registro_w + 1;
			delete from gpi_cron_etapa where nr_sequencia = c02_w.nr_sequencia;

			if (qt_registro_w >= 100) then
				begin
				commit;
				qt_registro_w := 0;
				end;
			end if;
			end;
		end loop;
		close c02;
		end;
	end loop;
	close c01;
	commit;
end if;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
