CREATE OR REPLACE PROCEDURE gpi_fechar_cronograma ( nr_seq_cronograma_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

update	gpi_cronograma
set	dt_fechamento	= CASE WHEN ie_opcao_p='F' THEN  clock_timestamp() WHEN ie_opcao_p='E' THEN  null END ,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia 	= nr_seq_cronograma_p;

commit;

end;
$body$
