CREATE OR REPLACE PROCEDURE gpi_gerar_itens_solic_etapa ( cd_estabelecimento_p bigint, nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proj_gpi_w				bigint;
nr_seq_etapa_gpi_w			bigint;
nr_seq_orcamento_w			bigint;
nr_item_solic_compra_w			bigint	:= 0;
cd_local_estoque_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_seq_orcamento,
	a.nr_sequencia,
	a.cd_material,
	a.cd_unidade_medida,
	a.qt_item,
	coalesce(a.vl_unitario,0) vl_unitario,
	a.nr_sequencia nr_seq_orc_item_gpi
from	gpi_orcamento b,
	gpi_orc_item a
where	b.nr_sequencia		= a.nr_seq_orcamento
and	b.nr_seq_projeto		= nr_seq_proj_gpi_w
and	a.nr_seq_etapa		= nr_seq_etapa_gpi_w
and	coalesce(a.qt_item, 0)		<> 0
and	(a.cd_material IS NOT NULL AND a.cd_material::text <> '')
and	coalesce(a.vl_realizado,0) = 0
and coalesce(a.ie_situacao,'I') = 'A'

union all

select	a.nr_seq_orcamento,
	a.nr_sequencia,
	a.cd_material,
	a.cd_unidade_medida,
	a.qt_item,
	coalesce(a.vl_unitario,0) vl_unitario,
	a.nr_sequencia nr_seq_orc_item_gpi
from	gpi_orcamento b,
	gpi_orc_item a
where	b.nr_sequencia		= a.nr_seq_orcamento
and	b.nr_seq_projeto		= nr_seq_proj_gpi_w
and	b.nr_sequencia		= nr_seq_orcamento_w
and	coalesce(a.qt_item, 0)		<> 0
and	nr_seq_etapa_gpi_w	= 0 /*Sem etapa informada*/
and	(a.cd_material IS NOT NULL AND a.cd_material::text <> '')
and	coalesce(a.vl_realizado,0) = 0
and coalesce(a.ie_situacao,'I') = 'A';

Vet01	C01%RowType;


BEGIN

select	max(nr_seq_proj_gpi),
	coalesce(max(nr_seq_etapa_gpi), 0),
	max(nr_seq_orcamento_gpi),
	max(cd_local_estoque)
into STRICT	nr_seq_proj_gpi_w,
	nr_seq_etapa_gpi_w,
	nr_seq_orcamento_w,
	cd_local_estoque_w
from	solic_compra
where	nr_solic_compra	= nr_solic_compra_p;

open C01;
loop
fetch C01 into
	Vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_item_solic_compra_w	:= nr_item_solic_compra_w + 1;
	insert into solic_compra_item(
		nr_solic_compra,
		nr_item_solic_compra,
		cd_material,
		cd_unidade_medida_compra,
		qt_material,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		ds_material_direto,
		ds_observacao,
		dt_solic_item,
		vl_unit_previsto,
		ie_geracao,
		qt_conv_compra_est_orig,
		nr_seq_orc_item_gpi,
		qt_saldo_disp_estoque)
	values (	nr_solic_compra_p,
		nr_item_solic_compra_w,
		Vet01.cd_material,
		vet01.cd_unidade_medida,
		Vet01.qt_item,
		clock_timestamp(),
		nm_usuario_p,
		'A',
		'',
		WHEB_MENSAGEM_PCK.get_texto(277226) || Vet01.nr_seq_orcamento || WHEB_MENSAGEM_PCK.get_texto(277227) || Vet01.nr_sequencia,
		clock_timestamp(),
		Vet01.vl_unitario,
		'S',
		obter_dados_material(Vet01.cd_material,'QCE'),
		Vet01.nr_seq_orc_item_gpi,
		obter_saldo_disp_estoque(cd_estabelecimento_p, Vet01.cd_material, cd_local_estoque_w, trunc(clock_timestamp(),'mm')));

	CALL gerar_solic_item_entrega(nr_solic_compra_p, nr_item_solic_compra_w, nm_usuario_p);

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
