CREATE OR REPLACE PROCEDURE gpi_iniciar_ativ_cronograma ( nr_sequencia_p bigint, dt_inicio_real_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (trunc(coalesce(dt_inicio_real_p,clock_timestamp())) > trunc(clock_timestamp())) then
	/*A data de início da atividade não pode ser superior a data atual!*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266519);
end if;
update	gpi_cron_etapa
set	dt_inicio_real	= coalesce(dt_inicio_real_p,clock_timestamp()),
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

CALL gpi_atualizar_etapa_superior(nr_sequencia_p,nm_usuario_p);

commit;

end;
$body$
