SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_liberar_estudo_viab ( nr_sequencia_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


/* IE_OPERACAO_P
L - Liberar - marca dt_liberacao
E - Estorna a liberação, passa dt_liberacao para null
*/
BEGIN

if (ie_operacao_p = 'L') then
	update	gpi_estudo_viab
	set	dt_liberacao	= clock_timestamp(),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_operacao_p = 'E') then
	update	gpi_estudo_viab
	set	dt_liberacao	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_p;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
