SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_liberar_orcamento ( nr_seq_orcamento_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w			bigint;
nr_seq_cronograma_w		bigint;


BEGIN

select	max(nr_seq_cronograma)
into STRICT	nr_seq_cronograma_w
from	gpi_orcamento
where	nr_sequencia = nr_seq_orcamento_p;

if (nr_seq_cronograma_w IS NOT NULL AND nr_seq_cronograma_w::text <> '') then

	select	count(*)
	into STRICT	qt_registro_w
	from	gpi_cron_etapa b,
		gpi_orc_item a
	where	a.nr_seq_etapa = b.nr_sequencia
	and	b.nr_seq_cronograma <> nr_seq_cronograma_w
	and	a.nr_seq_orcamento   = nr_seq_orcamento_p;

	if (qt_registro_w > 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(279671);
	end if;
end if;

if (ie_operacao_p = 'L') then

	update	gpi_orcamento
	set	dt_liberacao 	= clock_timestamp(),
		nm_usuario_lib	= nm_usuario_p
	where	nr_sequencia	= nr_seq_orcamento_p;

else
	update	gpi_orcamento
	set	dt_liberacao 	 = NULL,
		nm_usuario_lib	 = NULL
	where	nr_sequencia	= nr_seq_orcamento_p;
end if;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
