SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_liberar_risco ( nr_seq_risco_p bigint, ie_opcao_p text) AS $body$
DECLARE


/* ie_opcao_p
L - liberar
E - estornar*/
qt_registro_w	smallint;
qt_reg_w	smallint;


BEGIN

if (upper(ie_opcao_p) = 'L') then

select 	count(*)
into STRICT	qt_registro_w
from 	GPI_RISCO_PROJETO
where	nr_sequencia = nr_seq_risco_p
and	coalesce(dt_liberacao::text, '') = '';

	if (qt_registro_w > 0) then
		update GPI_RISCO_PROJETO
		set	dt_liberacao = to_char(clock_timestamp(),'dd/mm/yyyy')
		where	nr_sequencia = nr_seq_risco_p;

		select 	count(*)
		into STRICT 	qt_reg_w
		from	GPI_RISCO_PROJETO
		where 	nr_sequencia = nr_seq_risco_p
		and	coalesce(dt_fim_real::text, '') = '';

		if (qt_reg_w > 0) then
			update GPI_RISCO_PROJETO
			set	dt_fim_real = to_char(clock_timestamp(),'dd/mm/yyyy')
			where	nr_sequencia = nr_seq_risco_p;
		end if;
	end if;
end if;

if (upper(ie_opcao_p) = 'E') then

	select 	count(*)
	into STRICT	qt_registro_w
	from 	GPI_RISCO_PROJETO
	where	nr_sequencia = nr_seq_risco_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if ((qt_registro_w > 0) and (upper(ie_opcao_p) = 'E')) then
		update GPI_RISCO_PROJETO
		set	dt_liberacao  = NULL
		where	nr_sequencia = nr_seq_risco_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gpi_liberar_risco ( nr_seq_risco_p bigint, ie_opcao_p text) FROM PUBLIC;

