CREATE OR REPLACE PROCEDURE gpi_vincular_nota_fiscal ( nr_seq_nota_p bigint, nr_item_nf_p bigint, nr_seq_proj_gpi_p bigint, nr_seq_etapa_gpi_p bigint, ie_operacao_p text, nr_seq_orc_item_gpi_p bigint, nm_usuario_p text, ie_commit_p text default 'S') AS $body$
DECLARE


nr_seq_orc_item_gpi_w	bigint;
				

BEGIN

nr_seq_orc_item_gpi_w := nr_seq_orc_item_gpi_p;
if (nr_seq_orc_item_gpi_p = 0) then
	nr_seq_orc_item_gpi_w	:= null;
end if;

if (ie_operacao_p = 'V') then
	update	nota_fiscal_item
	set	nr_seq_proj_gpi		= nr_seq_proj_gpi_p,
		nr_seq_etapa_gpi	= nr_seq_etapa_gpi_p,
		nr_seq_orc_item_gpi	= nr_seq_orc_item_gpi_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_nota_p
	and	nr_item_nf		= nr_item_nf_p;
	
	update	titulo_pagar
	set	nr_seq_proj_gpi		= nr_seq_proj_gpi_p,
		nr_seq_etapa_gpi	= nr_seq_etapa_gpi_p,
		nr_seq_orc_item_gpi	= nr_seq_orc_item_gpi_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_nota_fiscal	= nr_seq_nota_p;

elsif (ie_operacao_p = 'D') then /* Desfazer o vinculo da NF*/
	update	nota_fiscal_item
	set	nr_seq_proj_gpi		 = NULL,
		nr_seq_etapa_gpi		 = NULL,
		nr_seq_orc_item_gpi	 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_nota_p;
	
	update	nota_fiscal_item_rateio
	set	nr_seq_orc_item_gpi	 = NULL
	where	nr_seq_nota		= nr_seq_nota_p;
	
	update	titulo_pagar
	set	nr_seq_proj_gpi		 = NULL,
		nr_seq_etapa_gpi	 = NULL,
		nr_seq_orc_item_gpi	 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_nota_fiscal	= nr_seq_nota_p;

end if;

if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
