SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_vincular_ordem_compra ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_proj_gpi_p bigint, nr_seq_etapa_gpi_p bigint, ie_operacao_p text, nr_seq_orc_item_gpi_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_orc_item_gpi_w	bigint;
ds_historico_w		varchar(4000) := '';
ds_etapa_gpi_w		varchar(100);
nr_seq_etapa_gpi_w	bigint;
ds_projeto_gpi_w	varchar(255);
nr_seq_projeto_gpi_w	bigint;
ds_novo_proj_gpi_w	varchar(255);
ds_novo_etapa_gpi_w	varchar(100);
ds_material_w		varchar(100);


BEGIN

select	nr_seq_etapa_gpi,
	substr(gpi_obter_desc_etapa(nr_seq_etapa_gpi),1,100) ds_etapa_gpi,
	substr(gpi_obter_desc_etapa(nr_seq_etapa_gpi_p),1,100) ds_novo_etapa_gpi,
	nr_seq_proj_gpi,
	gpi_obter_dados_projeto(nr_seq_proj_gpi, 'N') ds_projeto_gpi,
	gpi_obter_dados_projeto(nr_seq_proj_gpi_p, 'N') ds_novo_proj_gpi,
	substr(obter_dados_material(cd_material,'D'),1,100) ds_material
into STRICT	nr_seq_etapa_gpi_w,
	ds_etapa_gpi_w,
	ds_novo_etapa_gpi_w,
	nr_seq_projeto_gpi_w,
	ds_projeto_gpi_w,
	ds_novo_proj_gpi_w,
	ds_material_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

nr_seq_orc_item_gpi_w := nr_seq_orc_item_gpi_p;
if (nr_seq_orc_item_gpi_p = 0) then
	nr_seq_orc_item_gpi_w	:= null;
end if;

if (ie_operacao_p = 'V') then
	begin
	update	ordem_compra_item
	set	nr_seq_proj_gpi = nr_seq_proj_gpi_p,
		nr_seq_etapa_gpi = nr_seq_etapa_gpi_p,
		nr_seq_orc_item_gpi = nr_seq_orc_item_gpi_w,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;

	ds_historico_w := substr(wheb_mensagem_pck.get_texto(312092,'NR_ITEM_OCI=' || nr_item_oci_p ||
				';DS_MATERIAL=' || ds_material_w ||
				';NR_SEQ_PROJETO_GPI=' || nr_seq_projeto_gpi_w ||
				';DS_PROJETO_GPI=' || ds_projeto_gpi_w ||
				';NR_SEQ_PROJ_GPI=' || nr_seq_proj_gpi_p ||
				';DS_NOVO_PROJ_GPI=' || ds_novo_proj_gpi_w ||
				';NR_SEQ_ETAPA_GPI=' || nr_seq_etapa_gpi_w ||
				';DS_ETAPA_GPI=' || ds_etapa_gpi_w ||
				';NR_SEQ_ETAPA_GPI_P=' || nr_seq_etapa_gpi_p ||
				';DS_NOVO_ETAPA_GPI=' || ds_novo_etapa_gpi_w),1,4000);

	end;
elsif (ie_operacao_p = 'D') then
	begin
	update	ordem_compra_item
	set	nr_seq_proj_gpi  = NULL,
		nr_seq_etapa_gpi  = NULL,
		nr_seq_orc_item_gpi  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_ordem_compra = nr_ordem_compra_p
	and	nr_item_oci = nr_item_oci_p;

	ds_historico_w := substr(wheb_mensagem_pck.get_texto(312092,'NR_ITEM_OCI=' || nr_item_oci_p ||
				';DS_MATERIAL=' || ds_material_w ||
				';NR_SEQ_PROJETO_GPI=' || nr_seq_projeto_gpi_w ||
				';DS_PROJETO_GPI=' || ds_projeto_gpi_w ||
				';NR_SEQ_PROJ_GPI=' || nr_seq_proj_gpi_p ||
				';DS_NOVO_PROJ_GPI=' || ds_novo_proj_gpi_w ||
				';NR_SEQ_ETAPA_GPI=' || nr_seq_etapa_gpi_w ||
				';DS_ETAPA_GPI=' || ds_etapa_gpi_w ||
				';NR_SEQ_ETAPA_GPI_P=' || nr_seq_etapa_gpi_p ||
				';DS_NOVO_ETAPA_GPI=' || ds_novo_etapa_gpi_w),1,4000);

	end;
end if;

CALL gerar_ordem_compra_hist(nr_ordem_compra_p,
			null,
			substr(wheb_mensagem_pck.get_texto(312096),1,255),
			ds_historico_w,
			nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
