CREATE OR REPLACE PROCEDURE gpi_vincular_ordem_risco (nr_sequencia_p bigint, nr_seq_risco_gpi_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;


BEGIN

nr_sequencia_w := nr_sequencia_p;

if (ie_operacao_p = 'V') then
	begin
	update	man_ordem_servico
	set	nr_seq_risco_gpi	= nr_seq_risco_gpi_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_w;
	end;
elsif (ie_operacao_p = 'D') then
	begin
	update	man_ordem_servico
	set	nr_seq_risco_gpi	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_w;
	end;
end if;

commit;

END;
$body$
