CREATE OR REPLACE PROCEDURE gpi_vincular_ordem_servico ( nr_seq_ordem_serv_p bigint, nr_seq_proj_gpi_p bigint, nr_seq_etapa_gpi_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
V - Vincular
D - Desvincular
*/
BEGIN

update	man_ordem_servico
set	nr_seq_proj_gpi		= CASE WHEN ie_operacao_p='V' THEN  nr_seq_proj_gpi_p  ELSE null END ,
	nr_seq_etapa_gpi		= CASE WHEN ie_operacao_p='V' THEN  nr_seq_etapa_gpi_p  ELSE null END ,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_ordem_serv_p;

CALL gpi_atualizar_real_ativ(	nr_seq_ordem_serv_p,
			nr_seq_etapa_gpi_p,
			nm_usuario_p);

commit;

end;
