CREATE OR REPLACE PROCEDURE gpi_vincular_requisicao ( nr_requisicao_p bigint, nr_sequencia_p bigint, nr_seq_proj_gpi_p bigint, nr_seq_etapa_gpi_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
IE_OPERACAO_P
R - Requisição ao projeto
I - Item a uma atividade
*/
BEGIN

if (ie_operacao_p = 'R') then

	update	requisicao_material
	set	nr_seq_proj_gpi	= nr_seq_proj_gpi_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_requisicao	= nr_requisicao_p;

elsif (ie_operacao_p = 'I') then
	update	item_requisicao_material
	set	nr_seq_etapa_gpi	= nr_seq_etapa_gpi_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_requisicao		= nr_requisicao_p
	and	nr_sequencia		= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
