SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_vincular_solic_compra ( nr_solic_compra_p bigint, nr_seq_proj_gpi_p bigint, nr_seq_etapa_gpi_p bigint, ie_operacao_p text, nm_usuario_p text, nr_seq_orc_item_gpi_p bigint, nr_item_solic_compra_p bigint) AS $body$
BEGIN

if (ie_operacao_p = 'V') then
	
	update	solic_compra
	set	nr_seq_proj_gpi		= nr_seq_proj_gpi_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_solic_compra		= nr_solic_compra_p;
	
	if (coalesce(nr_seq_etapa_gpi_p, 0) <> 0) and (coalesce(nr_item_solic_compra_p, 0) <> 0) then

		update 	solic_compra
		set 	nr_seq_etapa_gpi	= nr_seq_etapa_gpi_p
		where	nr_solic_compra		= nr_solic_compra_p;
	end if;
	
	if (nr_item_solic_compra_p <> 0)and (nr_seq_orc_item_gpi_p <> 0)then	

		update  solic_compra_item
		set 	nr_seq_orc_item_gpi 	= nr_seq_orc_item_gpi_p	
		where	nr_solic_compra		= nr_solic_compra_p
		and	nr_item_solic_compra	= nr_item_solic_compra_p;
		
	elsif (nr_item_solic_compra_p = 0)and (nr_seq_orc_item_gpi_p <> 0)then

		update  solic_compra_item
		set 	nr_seq_orc_item_gpi 	= nr_seq_orc_item_gpi_p	
		where	nr_solic_compra		= nr_solic_compra_p;
	end if;
	
elsif (ie_operacao_p = 'D') then /* Desfazer o vinculo da NF*/
	
	update	solic_compra
	set	nr_seq_proj_gpi		 = NULL,
		nr_seq_etapa_gpi	 = NULL,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_solic_compra		= nr_solic_compra_p;
	
	update  solic_compra_item
	set 	nr_seq_orc_item_gpi 	 = NULL,
		dt_atualizacao		= clock_timestamp()
	where	nr_solic_compra		= nr_solic_compra_p;
	
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
