SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpi_vincular_titulo_pagar ( nr_titulo_p bigint, nr_seq_proj_gpi_p bigint, nr_seq_etapa_gpi_p bigint, nr_seq_conta_gpi_p bigint, ie_operacao_p text, nm_usuario_p text, nr_seq_orc_item_gpi_p bigint, nr_seq_classif_p bigint, ie_tipo_vinculo_p bigint) AS $body$
BEGIN
if (coalesce(ie_tipo_vinculo_p,0) = 0) then
	if (ie_operacao_p = 'V') then

		update	titulo_pagar
		set	nr_seq_proj_gpi	= nr_seq_proj_gpi_p,
			nr_seq_etapa_gpi	= nr_seq_etapa_gpi_p,
			nr_seq_conta_gpi	= nr_seq_conta_gpi_p,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			nr_seq_orc_item_gpi	=	nr_seq_orc_item_gpi_p
		where	nr_titulo		= nr_titulo_p;

	elsif (ie_operacao_p = 'D') then /* Desvincular título*/
		update	titulo_pagar
		set	nr_seq_proj_gpi	 = NULL,
			nr_seq_etapa_gpi	 = NULL,
			nr_seq_conta_gpi	 = NULL,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			nr_seq_orc_item_gpi	 = NULL
		where	nr_titulo		= nr_titulo_p;
	end if;

elsif (ie_tipo_vinculo_p = 1)then
	if (ie_operacao_p = 'V') then

		update	titulo_pagar_classif
		set	nr_seq_proj_gpi		= nr_seq_proj_gpi_p,
			nr_seq_etapa_gpi	= nr_seq_etapa_gpi_p,
			nr_seq_conta_gpi	= nr_seq_conta_gpi_p,
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			nr_seq_orc_item_gpi	= nr_seq_orc_item_gpi_p
		where	nr_titulo		= nr_titulo_p
		and	nr_sequencia		= nr_seq_classif_p;

	elsif (ie_operacao_p = 'D') then /* Desvincular título*/
		update	titulo_pagar_classif
		set	nr_seq_proj_gpi	 = NULL,
			nr_seq_etapa_gpi	 = NULL,
			nr_seq_conta_gpi	 = NULL,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			nr_seq_orc_item_gpi	 = NULL
		where	nr_titulo		= nr_titulo_p
		and	nr_sequencia		= nr_seq_classif_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
