CREATE OR REPLACE PROCEDURE gpto_group_items ( nr_prescricao_p prescr_material.nr_prescricao%type, nr_sequencia_p prescr_material.nr_sequencia%type, ie_agrupador_p prescr_material.ie_agrupador%type, nr_agrupamento_p prescr_material.nr_agrupamento%type, cd_intervalo_p prescr_material.cd_intervalo%type, nr_sequencias_p text ) AS $body$
BEGIN

    UPDATE  prescr_material
    SET     ie_item_superior 		=   'S'
    WHERE   nr_prescricao 		=   nr_prescricao_p
    AND     nr_sequencia 		=   nr_sequencia_p
    AND     ie_agrupador 		=   ie_agrupador_p;

    UPDATE  prescr_material
    SET     nr_agrupamento 		=   nr_agrupamento_p,
            cd_intervalo 		=   cd_intervalo_p,
            ie_item_superior 		=   'N'
    WHERE   nr_prescricao 		=   nr_prescricao_p
    AND     nr_sequencia 		!=  nr_sequencia_p
    AND     ie_agrupador 		=   ie_agrupador_p
    AND     nr_sequencia        	IN (WITH RECURSIVE cte AS (
SELECT regexp_substr(nr_sequencias_p, '[^,]+', 1, level) AS nr_sequencia

                                        (regexp_substr(nr_sequencias_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencias_p, '[^,]+', 1, level))::text <> '')  UNION ALL
SELECT regexp_substr(nr_sequencias_p, '[^,]+', 1, level) AS nr_sequencia
                                        
                                        (regexp_substr(nr_sequencias_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencias_p, '[^,]+', 1, level))::text <> '') JOIN cte c ON ()

) SELECT * FROM cte;
);

    DELETE FROM prescr_material
    WHERE       nr_prescricao           =   nr_prescricao_p
    AND         nr_sequencia_diluicao   !=  nr_sequencia_p
    AND         ie_agrupador            IN (3,7,9)
    AND         nr_sequencia_diluicao   IN (WITH RECURSIVE cte AS (
SELECT regexp_substr(nr_sequencias_p, '[^,]+', 1, level) AS nr_sequencia
                                        
                                        (regexp_substr(nr_sequencias_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencias_p, '[^,]+', 1, level))::text <> '')  UNION ALL
SELECT regexp_substr(nr_sequencias_p, '[^,]+', 1, level) AS nr_sequencia
                                        
                                        (regexp_substr(nr_sequencias_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencias_p, '[^,]+', 1, level))::text <> '') JOIN cte c ON ()

) SELECT * FROM cte;
);

    COMMIT;

END;
