SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gpt_ajustar_validade (nr_seq_item_p bigint, ie_tipo_item_p text, ie_opcao_p text) AS $body$
DECLARE


/*
ADF = Atualiza data fim
RHV = Remove hor√°rios que estao fora da validade
*/
ds_horarios_w				varchar(2000);
ds_horarios2_w				varchar(2000);
nr_ocorrencia_w 	numeric(20);
nr_atendimento_w 	numeric(20);
cd_intervalo_w 	varchar(200);
cd_material_w 	numeric(20);
dt_inicio_w 	timestamp;


BEGIN

if (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	if (ie_tipo_item_p = 'M') then	
	    if (ie_opcao_p = 'ADF') then
			update 	cpoe_material
			set 	dt_fim = ((dt_inicio + 1) - 1/86400),
					ds_horarios = ds_horarios_w
			where 	nr_sequencia = nr_seq_item_p;
			commit;
			
			select 	nr_atendimento,
					cd_intervalo,
					cd_material,
					dt_inicio				
			into STRICT 	nr_atendimento_w,
					cd_intervalo_w,
					cd_material_w,
					dt_inicio_w
			from 	cpoe_material
			where 	nr_sequencia = nr_seq_item_p;
			
		 	SELECT * FROM CPOE_Calcular_horario_prescr(nr_atendimento_w, cd_intervalo_w, cd_material_w, dt_inicio_w, null, null, nr_ocorrencia_w, ds_horarios_w, ds_horarios2_w, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_cd_perfil, null) INTO STRICT nr_ocorrencia_w, ds_horarios_w, ds_horarios2_w;

			update 	cpoe_material
			set 	ds_horarios = trim(both ds_horarios_w),
	                                                nr_ocorrencia = nr_ocorrencia_w
			where 	nr_sequencia = nr_seq_item_p;
			commit;
			
		elsif (ie_opcao_p = 'RHV') then
			select 	replace(ds_horarios, GPT_obter_se_horario_validade(nr_sequencia, ''), '')
			into STRICT 	ds_horarios_w
			from 	cpoe_material
			where 	nr_sequencia = nr_seq_item_p;
		
			update 	cpoe_material
			set 	ds_horarios = ds_horarios_w
			where 	nr_sequencia = nr_seq_item_p;
	
			commit;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
